import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Form, Alert } from "react-bootstrap";
import { Button } from "react-bootstrap";
import { useUserAuth } from "./UserAuthContext";
import './Auth.css'


const Signup = () => {
  const [name, setName] = useState("");

  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [role, setRole] = useState("");
  const [password, setPassword] = useState("");
  const { signUp } = useUserAuth();
  let navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {

      await signUp(name, email, password, role);
      navigate("/home");
    } catch (err) {
      setError(err.message);
    }
  };
  useEffect(() => {
    document.body.style.backgroundColor = "#feece98f";

    return () => {
      document.body.style.backgroundColor = "white";
    };
  }, []);

  return (
    <>

    <body>
    <section id="navbar">
        {/* <!-- Nav Bar --> */}
        <div class="container-fluid navbar-container">
            <nav class="navbar navbar-expand-lg navbar-dark" >
                <a class="navbar-brand brand" href="http://localhost:3500"><img class="logo-img" src={require("../global-assests/icon.png")}/>ForgetMeNot</a>
                <div class="collapse navbar-collapse" id="navbarToggler">
                <ul class="navbar-nav ms-auto">
                    <li class="nav-item">
                    <a class="nav-link" href="/login">Log In</a>
                    </li>
                    <li class="nav-item">
                    <a class="nav-link" href="">Teacher Tools</a>
                    </li>
                    <li class="nav-item">
                        {/* <!-- Sends user to teachers version of dashboard to acess student just change "teacher" to "student" this can be done in search bar or in code --> */}
                        <a class="nav-link" href="http://localhost:3500/teacher">Dashboard</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" href="http://localhost:3500/todolist">To Do List</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" href="http://localhost:3500">Home</a>
                    </li>
                </ul>
                </div>
                <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarToggler" aria-controls="navbarToggler" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
                </button>
            </nav>
        </div>

  </section>
  <section>
      <div className="p-4 box">
        <h2 >Signup</h2>
        {error && <Alert variant="danger">{error}</Alert>}

        <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formBasicName">
            <Form.Control
            required
              type="text"
              placeholder="Name"
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control
              type="email"
              placeholder="Email address"
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>


    <div key={`inline-radio`} className="mb-3">
      <Form.Check
        defaultChecked
        inline
        label="Student"
        name="role-group"
        type="radio"
        id={`role-1`}
        onChange={(e) => setRole(e.target.value)}
      />
      <Form.Check
        inline
        label="Teacher"
        name="role-group"
        type="radio"
        id={`role-2`}
        onChange={(e) => setRole(e.target.name)}
      />

    </div>




          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Control
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>

          <div className="d-grid gap-2">
            <Button variant="primary" type="Submit">
              Sign up
            </Button>
          </div>
        </Form>
      </div>
      <div className="p-4 box mt-3 text-center">
        Already have an account? <Link to="/login">Log In</Link>
      </div>
      </section>
      </body>
    </>
  );
};

export default Signup;
